import React, { createContext, useReducer } from 'react';
import AppReducer from './appReducer';


// Initial state
const initialState = {
  subs: [],
  allPlayers: [],
}

// context
export const GlobalContext = createContext(initialState);

// provider component
export const GlobalProvider = ({ children }) => {
  const [state, dispatch ] = useReducer(AppReducer, initialState );

  //actions
  function addSub (sub) {
    dispatch({
      type: 'ADD_SUB',
      payload: sub
    });
  }
  function deleteSub (sub) {
    dispatch({
      type: 'DELETE_SUB',
      payload: sub
    });
  }

  function addToAllPlayers (allPlayers) {
    dispatch({
      type: 'ADD_TOALLPLAYERS',
      payload: allPlayers
    });
  }
  // function deleteSingleEvent (singleEvent) {
  //   dispatch({
  //     type: 'delete_singleEvent',
  //     payload: singleEvent
  //   });
  // }
  // function editSingleEvent (singleEvent, index) {
  //   dispatch({
  //     type: 'edit_singleEvent',
  //     payload: singleEvent,
  //     editIndex: index
  //   });
  // }

  return  (
    // <GlobalContext.Provider value={{ items: state.items, addSingleEvent, deleteSingleEvent, editSingleEvent}}>
    <GlobalContext.Provider value={{ subs: state.subs, addSub, addToAllPlayers, deleteSub}}>
      {children}
    </GlobalContext.Provider>
  )
}
